package Day2;

public class SumofNaturalnoUsingWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int  i = 1, sum = 0;

	        while(i <=10)
	        {
	            sum += i;
	            i++;
	        }
	        System.out.println("Sum = " + sum);
	        int j=1, s=0;
	        do
	        {
	            s += j;
	            j++;
	        }while(j <=10);
	        System.out.println("Sum = " + s);
	}
	

}
